		if(!eval(model_name)){
			Ext.define(model_name, {
				extend: 'Ext.data.Model',
				fields: fields,
				idProperty: '_id'
			});
		}

		var store = Ext.create('Ext.data.Store', {
			model: model_name,
			data: { data: response.data },
			autoLoad: true,
			proxy: {
				type: 'memory',
				reader: {
					type: 'json',
					root: 'data'
				}
			}
		});

		me.getTransactionLogDetailLogGrid().reconfigure(store, columns);
