        cmb.showPrintersByFacility();

        if (cmb.store.find('id', cmb.getValue()) === cmb.getValue()) {
            // fix to stateful not selecting
            // cmb.setValue(cmb.getValue());
        } else {
            // cmb.store.getCount() > 0 ? cmb.setValue(cmb.store.first()) : cmb.setValue(null);
        }

